import { ConnectionType } from "./types";

export const getConnectionTypeSelectOptions = () => {
  return Object.values(ConnectionType).map((connectionValue) => {
    return {
      value: connectionValue,
      name: connectionTypeUiNameMap[connectionValue as ConnectionType],
    };
  });
};

export const getConnectionTypeLogo = (
  connectionType: string
): string | undefined => {
  switch (connectionType) {
    case ConnectionType.MYSQL:
      return getMySqlIcon();
    case ConnectionType.SNOWFLAKE:
      return getSnowFlakeIcon();
    case ConnectionType.TRINO:
      return getTrinoIcon();
    default:
      return undefined;
  }
};

const connectionTypeUiNameMap: Record<ConnectionType, string> = {
  [ConnectionType.MYSQL]: "MySQL",
  [ConnectionType.TRINO]: "Trino",
  [ConnectionType.SNOWFLAKE]: "Snowflake",
};

